INSERT [dbo].[detalles_salud] ([Id], [Id_salud], [Tipo], [Descripcion], [Imagen]) VALUES (257, 14, N'dosis', N'Desinfección por pulverización o inmersión de superficies:
Desinfección de rutina: diluya 1 litro de Vulkan en 332 litros de agua (0.3%) (Excepto Mycoplasma hyosynoviae, Enfermedad de Gumboro).
